package zhishusz.housepresell.service;import zhishusz.housepresell.controller.form.Empj_BuildingInfoForm;import zhishusz.housepresell.database.po.extra.MsgInfo;import zhishusz.housepresell.database.po.state.S_NormalFlag;import zhishusz.housepresell.util.MyBackInfo;import zhishusz.housepresell.util.MyProperties;import zhishusz.housepresell.util.project.HouseInfoUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Properties;import javax.transaction.Transactional;/* * Service添加操作：银行网点(开户行) * Company：ZhiShuSZ * */@Service@Transactionalpublic class Empj_BuildingGetPresellPriceService {	@Autowired    private HouseInfoUtil houseInfoUtil;    public Properties execute(Empj_BuildingInfoForm model) {        Properties properties = new MyProperties();        MsgInfo msgInfo = houseInfoUtil.calculatePresellSystemPrice(model.getTableId());        if(!msgInfo.isSuccess()){            return MyBackInfo.fail(properties, msgInfo.getInfo());        }        String result= (String) msgInfo.getExtra();        properties.put("presellPrice", result);        properties.put(S_NormalFlag.result, S_NormalFlag.success);        properties.put(S_NormalFlag.info, S_NormalFlag.info_Success);        return properties;    }}